import React, { useState } from "react";
import Card from "../UI/Card";
import styled from "styled-components";
import Button from "../UI/Button";


const AddUserArea = styled.form`
    /* display: flex; */
    width: 90%;
    max-width: 40rem;
    align-items: center;
    justify-content: center;
`

const Labels = styled.label`
    display: block; 
    font-weight: bold;
    margin: 0.5rem;
    /* margin-top: 0.5rem; */

`

const Inputs = styled.input`
    margin: 0.5rem ;
    padding: 1rem;
    width: 90%;
    max-width: 40rem;

    /* &input, input {
    font: inherit;
    display: block;
    width: 100%;
    border: 1px solid #ccc;
    padding: 0.15rem;
    margin-bottom: 0.5rem;
    } */

    &:focus {
    outline: none;
    border-color: #4f005f;
    }
`


const AddUser = (props) => {
    const [ enteredUsername, setEnteredUsername] = useState('');
    const [ enteredAge, setEnteredAge] = useState('');

    const usernameHandler = (event) => {
        setEnteredUsername(event.target.value);
    }

    const ageHandler = (event) => {
        setEnteredAge(event.target.value);
    }


    const addUserHandler = (event) => {
        event.preventDefault();

        if(enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
            // 밑에까지 내려가지 않고 그대로 끝내버린다.
            // trim()을 쓰면 문자열 앞뒤 공백을 없애준다.
            return;
        }
        if(+ enteredAge < 1) {
            // 문자열앞에 +를 쓰면 숫자형이 된다.
            return;
        }
        console.log(enteredUsername, enteredAge);
        setEnteredUsername('');
        setEnteredAge('');
    }

    return (
        <Card>
            <AddUserArea onSubmit={addUserHandler}>
                <Labels htmlFor="usename" className="labels">Username</Labels>
                <Inputs 
                type="text" 
                id="username" 
                onChange={usernameHandler}
                value={enteredUsername}/>
                <Labels htmlFor="userage" className="labels">Age (Years)</Labels>
                <Inputs 
                type="number" 
                id="userage" 
                onChange={ageHandler}
                value={enteredAge}/>
                <Button />
            </AddUserArea>
        </Card>
    )
}

export default AddUser;
