import React from "react";
import Card from "../UI/Card";
import styled from "styled-components";

const Labels = styled.label`
    display: block; 
    font-weight: bold;
    /* margin-bottom: 0.5rem; */
    margin: 0.5rem;

`

const Inputs = styled.input`

    margin: 0.5rem auto;
    padding: 1rem;
    width: 90%;
    max-width: 40rem;

    &input, input {
    font: inherit;
    display: block;
    width: 100%;
    border: 1px solid #ccc;
    padding: 0.15rem;
    margin-bottom: 0.5rem;
    }

    &:focus {
    outline: none;
    border-color: #4f005f;
    }
`


const AddUser = (props) => {

    const addUserHandler = (event) =>{
        event.preventDefault();
    }

    return (
        <Card>
            <form onSubmit={addUserHandler}>
                <Labels htmlFor="usename" className="labels">Username</Labels>
                <Inputs type="text" id="username"/>
                <Labels htmlFor="userage" className="labels">Age (Years)</Labels>
                <Inputs type="number" id="userage"/>
                <button type="submit">Add User</button>
            </form>
        </Card>



    )
}

export default AddUser;
