import React from "react";
import Card from "../UI/Card";
import styled from "styled-components";

const AddUserArea = styled.form`

    &input {
    margin: 2rem auto;
    padding: 1rem;
    width: 90%;
    max-width: 40rem;
    }

    &.labels {
    display: block; 
    font-weight: bold;
    margin-bottom: 0.5rem;
    }

    &input input {
    font: inherit;
    display: block;
    width: 100%;
    border: 1px solid #ccc;
    padding: 0.15rem;
    margin-bottom: 0.5rem;
    }

    &input:focus {
    outline: none;
    border-color: #4f005f;
    }
`


const AddUser = (props) => {

    const addUserHandler = (event) =>{
        event.preventDefault();
    }

    return (
        <Card>
            <AddUserArea onSubmit={addUserHandler}>
                <label htmlFor="usename" className="labels">Username</label>
                <input type="text" id="username"/>
                <label htmlFor="userage" className="labels">Age (Years)</label>
                <input type="number" id="userage"/>
                <button type="submit">Add User</button>
            </AddUserArea>
        </Card>



    )
}

export default AddUser;
